from flask_wtf import FlaskFormfrom wtforms import PasswordField, SubmitField, EmailField, BooleanFieldfrom wtforms.validators import DataRequiredclass LoginForms(FlaskForm):    email = EmailField('Почта', validators=[DataRequired()])    password = PasswordField('Пароль', validators=[DataRequired()])    remember_me = BooleanField('Запонить меня')    submit = SubmitField('Войти')